import { useState, useEffect } from "react"
import { Link } from "react-router-dom";
import http from "../http"

export default function Home() {
    const [user, setUsers] = useState([]);

    useEffect(() => {
        fetchAllUsers();
    },[]);

    const fetchAllUsers = () => {
        http.get('/users').then(res=>{
            setUsers(res.data);
        })
    }

    return (
        <div>
            <h2>User Listing</h2>
            <table className="table">
                <thead>
                    <tr>
                        <th>Sno.</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {user.map((user,index)=>(
                        <tr key={user.id}>
                            <td>{++index}</td>
                            <td>{user.name}</td>
                            <td>{user.email}</td>
                            <td>
                                <Link className="btn btn-info" to={{ pathname:"/edit/"+user.id }}>Edit</Link>&nbsp;
                                <Link className="btn btn-primary" to={{ pathname:"/view/"+user.id }}>View</Link>&nbsp;
                                <button type="button" className="btn btn-danger" onClick={()=>{deleteUser(user.id)}}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    )
}
